const { randomUUID } = require('crypto');
const fs = require('fs');

// ─── HIER ANPASSEN ──────────────────────────────────────────
const surveysPath = '/data/surveys.json';
const baseUrl     = 'https://survey.bridge-brain.ai';
// ────────────────────────────────────────────────────────────

const beschreibung = ($input.first().json['Beschreibung'] ?? '').trim();
const kommentar    = ($input.first().json['Kommentar']    ?? '').trim();
const vorlage      = ($input.first().json['Vorlage']      ?? '').trim();

const newId = randomUUID();

let surveys = [];
if (fs.existsSync(surveysPath)) {
  surveys = JSON.parse(fs.readFileSync(surveysPath, 'utf8'));
}

surveys.push({
  id:          newId,
  template:    vorlage,
  description: beschreibung,
  comments:    kommentar,
  created:     new Date().toISOString().split('T')[0],
});

fs.writeFileSync(surveysPath, JSON.stringify(surveys, null, 2), 'utf8');

return [{ json: {
  success:     true,
  id:          newId,
  url:         `${baseUrl}/${vorlage}.html?survey=${newId}`,
  description: beschreibung,
  template:    vorlage,
} }];
